import React, { useState, useEffect } from 'react';
import './App.css';

interface StationData {
  name: string;
  services: string[];
}

interface DepartureData {
  service: string;
  destination: string;
  departureTime: number;
}

const App: React.FC = () => {
  const [stations, setStations] = useState<StationData[]>([]);
  const [departures, setDepartures] = useState<Record<string, DepartureData[]>>({});

  useEffect(() => {
    // Simulating API call to get stations
    const mockStations: StationData[] = [
      { name: "Hauptbahnhof", services: ["S1", "S2", "U1", "U2", "Bus 100"] },
      { name: "Marienplatz", services: ["S1", "S2", "U3", "U6"] },
      { name: "Sendlinger Tor", services: ["U1", "U2", "U3", "U6", "Bus 62"] },
    ];
    setStations(mockStations);

    // Simulating API call to get departures
    const mockDepartures: Record<string, DepartureData[]> = {
      "Hauptbahnhof": [
        { service: "S1", destination: "Ostbahnhof", departureTime: 2 },
        { service: "U2", destination: "Feldmoching", departureTime: 5 },
        { service: "Bus 100", destination: "Ostbahnhof", departureTime: 8 },
      ],
      "Marienplatz": [
        { service: "S1", destination: "Freising", departureTime: 3 },
        { service: "U3", destination: "Moosach", departureTime: 6 },
      ],
      "Sendlinger Tor": [
        { service: "U1", destination: "Olympia-Einkaufszentrum", departureTime: 4 },
        { service: "Bus 62", destination: "Rotkreuzplatz", departureTime: 7 },
      ],
    };
    setDepartures(mockDepartures);
  }, []);

  return (
    <div className="app">
      {stations.map((station) => (
        <div key={station.name} className="station-section">
          <h2 className="station-name">{station.name}</h2>
          <p className="station-services">{station.services.join(' 路 ')}</p>
          <div className="departures">
            {departures[station.name]?.map((departure, index) => (
              <p key={index} className="departure-info">
                {departure.service} 路 {departure.destination} 路 {departure.departureTime} min
              </p>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
